  totalLearningPaths: Object.keys(learningPaths).length,
  totalCategories: Object.values(learningPaths).reduce((sum, path) => sum + Object.keys(path).length, 0),
  totalCourses: getTotalCourses(),
  totalLessons: getTotalLessons(),
